package com.pursuetao.fayelee0.leetcode.easy;

import org.junit.Test;

import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

public class TestCode389 {
    @Test
    public void findTheDifference1() {
        assertThat(Code389.findTheDifference1("abcd", "abcde"), is('e'));

        assertThat(Code389.findTheDifference1("",
                ""), is('t'));

    }

    @Test
    public void findTheDifference2() {
        assertThat(Code389.findTheDifference2("abcd", "abcde"), is('e'));

        assertThat(Code389.findTheDifference2("",
                ""), is('t'));

    }
}
